/**
 * Catalog product MAP "Display Actual Price" attribute source
 *
 * @category   Mage
 * @package    Mage_Catalog
 * @author     Magento Core Team <core@magentocommerce.com>
 */
class Shipfunk_Shipfunk_Model_Product_Attribute_Source_Environment
{
public function toOptionArray()
    {
        return array(
            array('value' => 1, 'label'=>Mage::helper('shipfunk_shipfunk')->__('Production')),
            array('value' => 0, 'label'=>Mage::helper('shipfunk_shipfunk')->__('Development')),
        );
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return array(
            0 => Mage::helper('shipfunk_shipfunk')->__('Development'),
            1 => Mage::helper('shipfunk_shipfunk')->__('Production'),
        );
    }
}
